import React from "react";

const AdminDashboard = () => {
  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-4">Admin Dashboard</h2>
      <div className="grid grid-cols-3 gap-4">
        <div className="bg-white p-4 rounded shadow">
          <h3 className="text-lg font-medium">Verify NGOs</h3>
          <p>Approve or reject NGO registrations.</p>
        </div>
        <div className="bg-white p-4 rounded shadow">
          <h3 className="text-lg font-medium">Approve Campaigns</h3>
          <p>Ensure campaign legitimacy before publishing.</p>
        </div>
        <div className="bg-white p-4 rounded shadow">
          <h3 className="text-lg font-medium">Reports</h3>
          <p>View donation trends, active NGOs, and total funds raised.</p>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
