import React from "react";

const NgoDashboard = () => {
  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-4">NGO Dashboard</h2>
      <div className="grid grid-cols-3 gap-4">
        <div className="bg-white p-4 rounded shadow">
          <h3 className="text-lg font-medium">Create Campaign</h3>
          <p>Start a new donation campaign with title, goal, and description.</p>
        </div>
        <div className="bg-white p-4 rounded shadow">
          <h3 className="text-lg font-medium">Manage Campaigns</h3>
          <p>View, edit, or delete your existing campaigns.</p>
        </div>
        <div className="bg-white p-4 rounded shadow">
          <h3 className="text-lg font-medium">Track Donations</h3>
          <p>Monitor donations received across campaigns.</p>
        </div>
      </div>
    </div>
  );
};

export default NgoDashboard;
