import React from "react";
import DashboardLayout from "../components/DashboardLayout";
import { Routes, Route } from "react-router-dom";
import CreateCampaign from "./ngo/CreateCampaign";
import ManageCampaigns from "./ngo/ManageCampaigns";

const NGODashboard = () => {
  const sidebarItems = [
    { label: "Create Campaign", path: "/ngo-dashboard/create" },
    { label: "Manage Campaigns", path: "/ngo-dashboard/manage" },
  ];

  return (
    <DashboardLayout sidebarItems={sidebarItems}>
      <Routes>
        <Route path="/" element={<p>Select an option from the sidebar.</p>} />
        <Route path="create" element={<CreateCampaign />} />
        <Route path="manage" element={<ManageCampaigns />} />
      </Routes>
    </DashboardLayout>
  );
};

export default NGODashboard;
