import React from "react";
import "../styles/Dashboard.css";
import { Routes, Route, Link } from "react-router-dom";
import CreateCampaign from "./ngo/CreateCampaign";
import ManageCampaigns from "./ngo/ManageCampaigns";

const NGODashboard = () => {
  return (
    <div className="dashboard-container">
      <aside className="sidebar">
        <nav>
          <ul>
            <li><Link to="/ngo-dashboard">Dashboard Home</Link></li>
            <li><Link to="/ngo-dashboard/create">Create Campaign</Link></li>
            <li><Link to="/ngo-dashboard/manage">Manage Campaigns</Link></li>
          </ul>
        </nav>
      </aside>
      <main className="main-content">
        <Routes>
          <Route path="/" element={
            <div className="dashboard-welcome">
              <h2>Welcome to NGO Dashboard</h2>
              <div className="quick-stats">
                <div className="stat-card">
                  <h3>Active Campaigns</h3>
                  <p>5</p>
                </div>
                <div className="stat-card">
                  <h3>Total Donations</h3>
                  <p>â‚¹45,000</p>
                </div>
              </div>
            </div>
          } />
          <Route path="/create" element={<CreateCampaign />} />
          <Route path="/manage" element={<ManageCampaigns />} />
        </Routes>
      </main>
    </div>
  );
};

export default NGODashboard;
